import React, { Component, useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Show() {
  useEffect(() => {
    topPopular();
    topRated();
    Upcoming();
  }, []);

  const [popular, setPopular] = useState([]);
  const [rated, setRated] = useState([]);
  const [upcoming, setUpcoming] = useState([]);

  const url = "https://www.themoviedb.org/movie/";
  const imgUrl = "https://www.themoviedb.org/t/p/w600_and_h900_bestv2/";

  const topPopular = async () => {
    const data = await fetch(
      "https://api.themoviedb.org/3/movie/popular?api_key=aa08d6c30dbc0f64983cfec52a0e8d7e&language=en-US&page=1"
    );
    const popular = await data.json();
    // console.log(popular.results);
    setPopular(popular.results);
  };

  const topRated = async () => {
    const data = await fetch(
      "https://api.themoviedb.org/3/movie/top_rated?api_key=aa08d6c30dbc0f64983cfec52a0e8d7e&language=en-US&page=1"
    );
    const rated = await data.json();
    // console.log(rated.results);
    setRated(rated.results);
  };

  const Upcoming = async () => {
    const data = await fetch(
      "https://api.themoviedb.org/3/movie/upcoming?api_key=aa08d6c30dbc0f64983cfec52a0e8d7e&language=en-US&page=1)"
    );
    const upcoming = await data.json();
    // console.log(upcoming.results);
    setUpcoming(upcoming.results);
  };

  return (
    <div className="Show">
      <div className="row">
        <h1>Top 10 Popular Movies</h1>
        <div className="movies">
          {popular.slice(0, 10).map((movie) => {
            return (
              <a href={url + movie.id} target="_blank">
                <img src={imgUrl + movie.poster_path} />
                <h3>{movie.title}</h3>
              </a>
            );
          })}
        </div>
      </div>
      <div className="row">
        <h1>Top 10 Top-Rated Movies</h1>
        {rated.slice(0, 10).map((movie) => {
          return (
            <a href={url + movie.id} target="_blank">
              <img src={imgUrl + movie.poster_path} />
              <h3>{movie.title}</h3>
            </a>
          );
        })}
      </div>
      <div className="row">
        <h1>Top 10 Upcoming Movies</h1>
        {upcoming.slice(0, 10).map((movie) => {
          return (
            <a href={url + movie.id} target="_blank">
              <img src={imgUrl + movie.poster_path} />
              <h3>{movie.title}</h3>
            </a>
          );
        })}
      </div>
    </div>
  );
}

export default Show;
