import React, { Component } from "react";
import data from "./data.json";

function Movie({ match }) {
  const movie = data.find((a) => a.slug === match.params.title);
  console.log(movie);
  let flag = true;
  if (movie === undefined) {
    flag = false;
  }
  console.log(flag);
  return flag ? (
    <div className="Movie">
      <h1>{movie.name}</h1>
    </div>
  ) : (
    <div className="Movie">
      <div className="invalidMovie">
        <h2>Invalid Movie Title.. </h2>
        <h2>Movie doesn't exist</h2>
      </div>
    </div>
  );
}

export default Movie;
