import React, { useState } from "react";
import "./index.css";
import image from "./logo.svg";
import nav from "./images/nav.png";

function Header() {
  const [navbar, setNav] = useState(false);

  const backgroundChanger = () => {
    if (window.scrollY >= 30) {
      setNav(true);
    } else {
      setNav(false);
    }
  };

  const navClick = () => {
