import React, { Component, useState, useEffect } from "react";
import api from "./movies_api.ts";
console.log(api);

function Show() {
  useEffect(() => {
    topPopular();
    topRated();
    Upcoming();
  }, []);

  const [popular, setPopular] = useState([]);
  const [rated, setRated] = useState([]);
  const [upcoming, setUpcoming] = useState([]);

  const url = "https://www.themoviedb.org/movie/";
  const imgUrl = "https://www.themoviedb.org/t/p/w600_and_h900_bestv2/";

  const topPopular = async () => {
    const data = await fetch(
      "https://api.themoviedb.org/3/movie/popular?api_key=" +
        api.key +
        "&language=en-US&page=1"
    );
    const popular = await data.json();
    // console.log(popular.results);
    setPopular(popular.results);
  };

  const topRated = async () => {
    const data = await fetch(
      "https://api.themoviedb.org/3/movie/top_rated?api_key=" +
        api.key +
        "&language=en-US&page=1"
    );
    const rated = await data.json();
    // console.log(rated.results);
    setRated(rated.results);
  };

  const Upcoming = async () => {
    const data = await fetch(
      "https://api.themoviedb.org/3/movie/upcoming?api_key=" +
        api.key +
        "&language=en-US&page=1)"
    );
    const upcoming = await data.json();
    // console.log(upcoming.results);
    setUpcoming(upcoming.results);
  };

  return (
    <div className="Show">
      <div className="row">
        <h1>Top 10 Popular Movies</h1>
        <div className="movies">
          {popular.slice(0, 5).map((movie) => {
            return (
              <a href={api.url + movie.id} target="_blank">
                <img src={api.imgUrl + movie.poster_path} />
                <h3>{movie.title}</h3>
              </a>
            );
          })}
        </div>
        <div className="movies">
          {popular.slice(5, 10).map((movie) => {
            return (
              <a href={url + movie.id} target="_blank">
                <img src={imgUrl + movie.poster_path} />
                <h3>{movie.title}</h3>
              </a>
            );
          })}
        </div>
      </div>
      <div className="row">
        <h1>Top 10 Top-Rated Movies</h1>
        <div className="movies">
          {rated.slice(0, 5).map((movie) => {
            return (
              <a href={url + movie.id} target="_blank">
                <img src={imgUrl + movie.poster_path} />
                <h3>{movie.title}</h3>
              </a>
            );
          })}
        </div>
        <div className="movies">
          {rated.slice(5, 10).map((movie) => {
            return (
              <a href={url + movie.id} target="_blank">
                <img src={imgUrl + movie.poster_path} />
                <h3>{movie.title}</h3>
              </a>
            );
          })}
        </div>
      </div>
      <div className="row">
        <h1>Top 10 Upcoming Movies</h1>
        <div className="movies">
          {upcoming.slice(0, 5).map((movie) => {
            return (
              <a href={url + movie.id} target="_blank">
                <img src={imgUrl + movie.poster_path} />
                <h3>{movie.title}</h3>
              </a>
            );
          })}
        </div>
        <div className="movies">
          {upcoming.slice(5, 10).map((movie) => {
            return (
              <a href={url + movie.id} target="_blank">
                <img src={imgUrl + movie.poster_path} />
                <h3>{movie.title}</h3>
              </a>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Show;
