import "./index.css";
import image from "./logo.svg";

let fixedHeader = document.querySelector(".Header");

function Header() {
  return (
    <div className="Header">
      <a href="#">
        <img src={image} width="154px" height="24" />
      </a>
      <div className="drop-down">
        <ul>
          <li>
            <a href="#" className="list">
              Movies
            </a>
          </li>
          <li>
            <a href="#" className="list">
              Tv Show
            </a>
          </li>
          <li>
            {" "}
            <a href="#" className="list">
              More
            </a>
          </li>
        </ul>
      </div>
    </div>
  );
}

window.onscroll = () => {
  if (window.pageYOffset > fixedHeader.offsetTop) {
    fixedHeader.classList.add("fixed");
  } else {
    fixedHeader.classList.remove("fixed");
  }
};

export default Header;
