import React, { Component } from "react";
import data from "./data.json";
import "./index.css";
import "./circle.css";

function Movie({ match }) {
  const movie = data.find((data) => data.slug === match.params.title);

  let flag = true;
  if (movie === undefined) {
    flag = false;
  }

  console.log(flag);
  return flag ? (
    <div className="Movie">
      <div className="validMovie">
        <div>
          <img src={movie.url} />
        </div>
        <section>
          <h2>Title: </h2>
          <h1>{movie.name}</h1>
          <div class={`c100 p${movie.rating} center small orange`}>
            <span>{movie.rating}%</span>
            <div class="slice">
              <div class="bar"></div>
              <div class="fill"></div>
            </div>
          </div>
        </section>
      </div>
    </div>
  ) : (
    <div className="Movie">
      <div className="invalidMovie">
        <h1>Invalid Movie Title.. </h1>
        <h1>Movie doesn't exist!</h1>
      </div>
    </div>
  );
}

export default Movie;
