import React, { Component, useState, useEffect } from "react";
import { Link } from "react-router-dom";

function Show() {
  useEffect(() => {
    topPopular();
  }, []);

  const [popular, setPopular] = useState([]);
  const imgUrl = "https://www.themoviedb.org/t/p/w600_and_h900_bestv2/";

  const topPopular = async () => {
    const data = await fetch(
      "https://api.themoviedb.org/3/movie/popular?api_key=aa08d6c30dbc0f64983cfec52a0e8d7e&language=en-US&page=1"
    );
    const popular = await data.json();
    console.log(popular.results);
    setPopular(popular.results);
  };

  return (
    <div className="Show">
      <div className="row">
        <h1>Top 10 Popular Movies</h1>
        {popular.slice(0, 10).map((movies, key) => {
          return (
            <Link to={``}>
              <img src={imgUrl + movies.poster_path} />
            </Link>
          );
        })}
      </div>
      <div className="row">
        <h1>Top 10 Top-Rated Movies</h1>
      </div>
      <div className="row">
        <h1>Top 10 Upcoming Movies</h1>
      </div>
    </div>
  );
}

export default Show;
